package Variables;

public class Primitive_DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Byte DataType
		System.out.println("*----Byte Data Type----*");
		
		byte myByte = 8;
        System.out.println("Initial value: " + myByte);
        
        myByte = 10;
        System.out.println("Modified value: " + myByte);
        
        //Short Data Type
        
        System.out.println("\n*----Short Data Type----*");
		
		short myShort = 500;
        System.out.println("Initial value: " + myShort);
        
        myShort = 890;
        System.out.println("Modified value: " + myShort);
        
        //Integer Data Type
        
        System.out.println("\n*----Integer Data Type----*");
		
		int myInt = 32000;
        System.out.println("Initial value: " + myInt);
        
        myInt = 35000;
        System.out.println("Modified value: " + myInt);
        
        //Long Data Type
        
        System.out.println("\n*----Long Data Type----*");
        
        long myLong = 1000000000L;
        System.out.println("Initial value: " + myLong);
        
        myLong = 2000000000L;
        System.out.println("Modified value: " + myLong);
        
        //Float Data Type
        
        System.out.println("\n*----Float Data Type----*");
        
        float myFloat = 2.74f;
        System.out.println("Initial value: " + myFloat);
        
        myFloat = 3.07f;
        System.out.println("Modified value: " + myFloat);
        
        //Double Data Type
        
        System.out.println("\n*----Double Data Type----*");
        
        double myDouble = 3.121529;
        System.out.println("Initial value: " + myDouble);
        
        myDouble = 2.718281;
        System.out.println("Modified value: " + myDouble);
        
        //Char Data Type
        
        System.out.println("\n*----Character Data Type----*");
        
        char myChar = 'N';
        System.out.println("Initial value: " + myChar);
        
        myChar = 'T';
        System.out.println("Modified value: " + myChar);
        
        //Boolean Data Type
        
        System.out.println("\n*----Boolean Data Type----*");
        
        boolean myBoolean = true;
        System.out.println("Initial Boolean: " + myBoolean);
        
        myBoolean = false;
        System.out.println("Modified Boolean: " + myBoolean);
        
	}

}
